import { Component, Inject, OnInit } from '@angular/core';
import { isPlatformBrowser } from '@angular/common';
import { PLATFORM_ID } from '@angular/core';
import { RouterOutlet } from '@angular/router';
import { FormsModule } from '@angular/forms';
import { NgIf } from '@angular/common';
import { initFlowbite } from 'flowbite'; // Asegúrate de tener importado 'flowbite' si es necesario

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  searchQuery: string = '';

  constructor(@Inject(PLATFORM_ID) private platformId: Object) {}

  ngOnInit(): void {
    if (isPlatformBrowser(this.platformId)) {
      initFlowbite(); // Inicializa Flowbite solo si estás en un navegador
    }
  }

  performSearch(): void {
    console.log('Search query:', this.searchQuery);
    // Aquí podrías agregar lógica adicional para realizar la búsqueda
  }
}
