import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common'; 
import { Router } from '@angular/router';
@Component({
  selector: 'app-navbar',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './navbar.component.html',
  styleUrl: './navbar.component.css'
})
export class NavbarComponent implements OnInit {
  firstName!: string;
  lastName!: string;
  accountType!: number;
  

  constructor(private router: Router) {}

  ngOnInit(): void {
    this.loadAccountType();
  }

  createActivity(){
    this.router.navigate(['/createActivities']);
  }
   Logout(){
    localStorage.clear();
    this.router.navigate(['/login'])
  }

  toHome(){
    this.router.navigate(['/'])
  }

  private loadAccountType(): void {
    const currentUserString = localStorage.getItem('currentUser');
    if (currentUserString) {
      try {
        const currentUser = JSON.parse(currentUserString);

        this.accountType = currentUser.accountType;
        this.firstName = currentUser.firstName;
        this.lastName = currentUser.lastName;

      } catch (error) {
        console.error('Error al parsear currentUser desde localStorage:', error);
        this.accountType = 0; 
      }
    } else {
      this.accountType = 0; 
    }
  }
}
<style>
    .header-logo {
      height: 4rem; /* Incrementado el tamaño del logo */
    }
    .search-bar {
      width: 50%;
      max-width: 600px;
      border-radius: 9999px;
      border: 1px solid #D1D5DB; /* Border color gray-300 */
    }
  </style>
  
<nav class="flex items-center justify-between p-4 bg-white shadow border-b border-gray-300">
    <div class="flex items-center space-x-4">
      <!-- Logo de ManosAmigas -->
      <img src="assets/MLogo.jpg" alt="ManosAmigas Logo" class="header-logo" (click)="toHome()">
    </div>
    
    <div class="flex justify-center flex-1">
      <!-- Barra de búsqueda -->
      <input type="text" placeholder="Buscar actividad..." class="p-2 search-bar">
    </div>
  
    <div class="flex items-center space-x-4">
      <!-- Iconos de mensajes y perfil -->
      <a *ngIf="accountType === 0" aria-label="Crear nueva actividad">
        <button class="p-2" (click)="createActivity()">
          <img src="assets/agregar.png" alt="Agregar actividad" class="h-6 w-6 md:h-8 md:w-8 lg:h-10 lg:w-10">
        </button>
      </a>
      
      <button data-popover-target="popover-user-profile" type="button" class="p-2"><img src="assets/login.png" alt="Profile" class="h-6 w-6 md:h-8 md:w-8 lg:h-10 lg:w-10"></button>

        <div data-popover id="popover-user-profile" role="tooltip" class="absolute z-10 invisible inline-block w-64 text-sm text-gray-500 transition-opacity duration-300 bg-white border border-gray-200 rounded-lg shadow-sm opacity-0 dark:text-gray-400 dark:bg-gray-800 dark:border-gray-600">
            <div class="p-3">

                <p class="mb-4 text-lg font-semibold leading-none text-gray-900 dark:text-white">
                   {{ firstName }} {{lastName}}
                </p>

                <p class="mb-5 text-base font-semibold leading-none text-gray-900 dark:text-white">
                  {{ accountType === 1 ? 'Voluntariado' : 'Host' }}
                </p>
                <div class="flex items-center justify-between mb-2">
                  <div>
                      <a *ngIf="accountType === 0"><button type="button" class=" text-sm text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg  px-3 py-1.5 dark:bg-blue-600 dark:hover:bg-blue-700 focus:outline-none dark:focus:ring-blue-800">Mis Actividades</button></a>
                  </div>
                  <div>
                    <button type="button" class=" text-sm text-white bg-red-700 hover:bg-red-800 focus:ring-4 focus:ring-red-300 font-medium rounded-lg px-3 py-1.5 dark:bg-red-600 dark:hover:bg-red-700 focus:outline-none dark:focus:ring-red-800" (click)="Logout()" >Cerrar Sesion</button>
                </div>
              </div>
            </div>
            <div data-popper-arrow></div>
        </div>

    </div>
  </nav>
