            <input type="text" formControlName="firstName" id="floating_first_name" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
            <label for="floating_first_name" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">First name</label>
            <div *ngIf="registerForm.controls['firstName'].invalid && registerForm.controls['firstName'].touched" class="text-red-600 text-sm">
                <div *ngIf="registerForm.controls['firstName'].errors?.['required']">First name is required</div>
            </div>
        </div>

        <div class="relative z-0 w-full mb-5 group">
            <input type="text" formControlName="lastName" id="floating_last_name" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
            <label for="floating_last_name" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Last name</label>
            <div *ngIf="registerForm.controls['lastName'].invalid && registerForm.controls['lastName'].touched" class="text-red-600 text-sm">
                <div *ngIf="registerForm.controls['lastName'].errors?.['required']">Last name is required</div>
            </div>
        </div>
      </div>

      <div class="relative z-0 w-full mb-5 group">
        <select formControlName="userType" id="user_type" class="block py-2.5 px-0 w-full text-sm text-gray-500 bg-transparent border-0 border-b-2 border-gray-200 appearance-none focus:outline-none focus:ring-0 focus:border-gray-500 peer">
          <option selected disabled>Select user type</option>
          <option value="organizer">Organizer</option>
          <option value="volunteer">Volunteer</option>
        </select>
        <div *ngIf="registerForm.controls['userType'].invalid && registerForm.controls['userType'].touched" class="text-red-600 text-sm">
            <div *ngIf="registerForm.controls['userType'].errors?.['required']">User type is required</div>
        </div>
      </div>

      <div class="relative z-0 w-full mb-5 group">
        <input type="email" formControlName="email" id="floating_email" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
        <label for="floating_email" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Email address</label>
        <div *ngIf="emailControl?.invalid && emailControl?.touched" class="text-red-600 text-sm">
            <div *ngIf="emailControl?.errors?.['required']">Email is required</div>
            <div *ngIf="emailControl?.errors?.['email']">Email is not valid</div>
        </div>
      </div>

      <div class="relative z-0 w-full mb-5 group">
        <input type="password" formControlName="password" id="floating_password" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
        <label for="floating_password" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Password</label>
        <div *ngIf="passwordControl?.invalid && passwordControl?.touched" class="text-red-600 text-sm">
            <div *ngIf="passwordControl?.errors?.['required']">Password is required</div>
            <div *ngIf="passwordControl?.errors?.['minlength']">Password must be at least 6 characters long</div>
        </div>
      </div>

      <div class="relative z-0 w-full mb-5 group">
        <input type="password" formControlName="repeatPassword" id="floating_repeat_password" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
        <label for="floating_repeat_password" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Confirm password</label>
        <div *ngIf="repeatPasswordControl?.invalid && repeatPasswordControl?.touched" class="text-red-600 text-sm">
            <div *ngIf="repeatPasswordControl?.errors?.['required']">Confirm password is required</div>
            <div *ngIf="registerForm.errors?.['mismatch']">Passwords do not match</div>
        </div>
      </div>
      
      <button 
        type="submit" 
        [ngClass]="{
            'bg-red-600 text-white hover:bg-red-500': registerForm.valid,
            'bg-gray-500 text-white cursor-not-allowed': !registerForm.valid
        }"
        [disabled]="registerForm.invalid"
        class="w-full flex justify-center p-4 mt-10 rounded-full tracking-wide font-semibold focus:outline-none transition ease-in duration-300">
        Register
      </button>
    </form>

    <p class="flex flex-col items-center justify-center mt-10 text-center text-md text-gray-500">
      <span>Already have an account?</span>
      <a routerLink="/login" class="text-indigo-500 hover:text-indigo-600 no-underline hover:underline cursor-pointer transition ease-in duration-300">Login</a>
    </p>
  </div>
</div>
