import { FormBuilder, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { Router, RouterModule } from '@angular/router';
import { RegisterService } from '../../services/register.service';

@Component({
  selector: 'app-register',
  standalone: true,
  imports: [ReactiveFormsModule, CommonModule, RouterModule],
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  registerForm: FormGroup;
  showToast: boolean = false;

  constructor(private fb: FormBuilder, private registerService: RegisterService, private router: Router) {
    this.registerForm = this.fb.group({
      firstName: ['', Validators.required],
      lastName: ['', Validators.required],
      userType: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      repeatPassword: ['', Validators.required]
    }, { validator: this.passwordMatchValidator });
  }

  passwordMatchValidator(form: FormGroup) {
    const password = form.get('password');
    const repeatPassword = form.get('repeatPassword');
    return password && repeatPassword && password.value === repeatPassword.value ? null : { mismatch: true };
  }

  get emailControl() {
    return this.registerForm.get('email');
  }

  get passwordControl() {
    return this.registerForm.get('password');
  }

  get repeatPasswordControl() {
    return this.registerForm.get('repeatPassword');
  }

  register() {
    if (this.registerForm.valid) {
      const formData = this.registerForm.value;
      const userTypeMapping: { [key: string]: number } = {
        organizer: 0,
        volunteer: 1
      };

      const accountType = userTypeMapping[formData.userType as keyof typeof userTypeMapping];

      const data = {
        firstName: formData.firstName,
        lastName: formData.lastName,
        email: formData.email,
        password: formData.password,
        accountType: accountType
      };

      this.registerService.register(data).subscribe(
        response => {
          console.log('Registration successful', response);
          this.showToast = true;
          setTimeout(() => {
            this.router.navigate(['/login']);
          }, 4500);
        },
        error => {
          console.error('Registration error', error);
        }
      );
    }
  }
}
<!-- component -->
<div class="min-h-screen flex justify-center py-12 px-4 sm:px-6 lg:px-8 bg-gray-500 bg-no-repeat bg-cover relative items-center"
     style="background-image: url('https://blog.oxfamintermon.org/wp-content/uploads/2015/03/voluntariado.jpg');">
  <div class="absolute bg-black opacity-60 inset-0 z-0"></div>
  <div class="max-w-md w-full space-y-8 p-10 bg-white rounded-xl z-10">
    <div class="text-center">
      <img src="assets/MLogo.jpg" alt="ManosAmigas Logo" class="header-logo h-24 mx-auto">
    </div>
    
    <form [formGroup]="registerForm" (ngSubmit)="register()" class="max-w-md mx-auto">
      <div class="grid md:grid-cols-2 md:gap-6">
        <div class="relative z-0 w-full mb-5 group">
            <input type="text" formControlName="firstName" id="floating_first_name" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
            <label for="floating_first_name" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Nombre</label>
            <div *ngIf="registerForm.controls['firstName'].invalid && registerForm.controls['firstName'].touched" class="text-red-600 text-sm">
                <div *ngIf="registerForm.controls['firstName'].errors?.['required']">Nombre es requerido</div>
            </div>
        </div>

        <div class="relative z-0 w-full mb-5 group">
            <input type="text" formControlName="lastName" id="floating_last_name" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
            <label for="floating_last_name" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Apellidos</label>
            <div *ngIf="registerForm.controls['lastName'].invalid && registerForm.controls['lastName'].touched" class="text-red-600 text-sm">
                <div *ngIf="registerForm.controls['lastName'].errors?.['required']">Apellido es requerido</div>
            </div>
        </div>
      </div>

      <div class="relative z-0 w-full mb-5 group">
        <select formControlName="userType" id="user_type" class="block py-2.5 px-0 w-full text-sm text-gray-500 bg-transparent border-0 border-b-2 border-gray-200 appearance-none focus:outline-none focus:ring-0 focus:border-gray-500 peer">
          <option disabled selected>Elije el tipo de cuenta</option>
          <option value="organizer">Host</option>
          <option value="volunteer">Voluntario</option>
          
        </select>
        <div *ngIf="registerForm.controls['userType'].invalid && registerForm.controls['userType'].touched" class="text-red-600 text-sm">
            <div *ngIf="registerForm.controls['userType'].errors?.['required']">El tipo de cuenta es necesario</div>
        </div>
      </div>

      <div class="relative z-0 w-full mb-5 group">
        <input type="email" formControlName="email" id="floating_email" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
        <label for="floating_email" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Correo Electronico</label>
        <div *ngIf="emailControl?.invalid && emailControl?.touched" class="text-red-600 text-sm">
            <div *ngIf="emailControl?.errors?.['required']">Correo Electronico es requerido</div>
            <div *ngIf="emailControl?.errors?.['email']">Correo Electronico no valido</div>
        </div>
      </div>

      <div class="relative z-0 w-full mb-5 group">
        <input type="password" formControlName="password" id="floating_password" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
        <label for="floating_password" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Contraseña</label>
        <div *ngIf="passwordControl?.invalid && passwordControl?.touched" class="text-red-600 text-sm">
            <div *ngIf="passwordControl?.errors?.['required']">La contraseña es requerida</div>
            <div *ngIf="passwordControl?.errors?.['minlength']">La contraseña debe tener minimo 8 digitos</div>
        </div>
      </div>

      <div class="relative z-0 w-full mb-5 group">
        <input type="password" formControlName="repeatPassword" id="floating_repeat_password" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
        <label for="floating_repeat_password" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Confirmar Contraseña</label>
        <div *ngIf="repeatPasswordControl?.invalid && repeatPasswordControl?.touched" class="text-red-600 text-sm">
            <div *ngIf="repeatPasswordControl?.errors?.['required']">Contraseña de confirmacion es requerido</div>
            <div *ngIf="registerForm.errors?.['mismatch']">Las contraseñas no concuerdan</div>
        </div>
      </div>
      
      <button 
        type="submit" 
        [ngClass]="{
            'bg-red-600 text-white hover:bg-red-500': registerForm.valid,
            'bg-gray-500 text-white cursor-not-allowed': !registerForm.valid
        }"
        [disabled]="registerForm.invalid"
        class="w-full flex justify-center p-4 mt-10 rounded-full tracking-wide font-semibold focus:outline-none transition ease-in duration-300">
        Registrar
      </button>
    </form>

    <div *ngIf="showToast" id="toast-success" class="flex items-center w-full max-w-xs p-4 mb-4 text-gray-500 bg-white rounded-lg shadow dark:text-gray-400 dark:bg-gray-800" role="alert">
      <div class="inline-flex items-center justify-center flex-shrink-0 w-8 h-8 text-green-500 bg-green-100 rounded-lg dark:bg-green-800 dark:text-green-200">
          <svg class="w-5 h-5" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 20 20">
              <path d="M10 .5a9.5 9.5 0 1 0 9.5 9.5A9.51 9.51 0 0 0 10 .5Zm3.707 8.207-4 4a1 1 0 0 1-1.414 0l-2-2a1 1 0 0 1 1.414-1.414L9 10.586l3.293-3.293a1 1 0 0 1 1.414 1.414Z"/>
          </svg>
          <span class="sr-only">Check icon</span>
      </div>
      <div class="ms-3 text-sm font-normal"> Usuario registrado correctamente, será rederijido a iniciar sesión.</div>
  </div>

    <p class="flex flex-col items-center justify-center mt-10 text-center text-md text-gray-500">
      <span>Ya tiene una cuenta?</span>
      <a [routerLink]="'/login'" class="text-indigo-500 hover:text-indigo-600 no-underline hover:underline cursor-pointer transition ease-in duration-300">Inciar sesion</a>
    </p>
  </div>
</div>
