<div class="min-h-screen flex justify-center py-12 px-4 sm:px-6 lg:px-8 bg-gray-500 bg-no-repeat bg-cover relative items-center"
     style="background-image: url(https://www.banreservas.com/SiteAssets/Nosotros%20Media/Voluntariado/Actividades/reforestar_Voluntariado.jpg);">
  <div class="absolute bg-black opacity-60 inset-0 z-0"></div>
  <div class="max-w-md w-full space-y-8 p-10 bg-white rounded-xl z-10">
    <div class="text-center">
      <h4 class="text-2xl font-bold dark:text-white">Crear Actividades</h4>
    </div>
    
    <form [formGroup]="activityForm" (ngSubmit)="onSubmit()" class="max-w-md mx-auto">
      
      <div class="relative z-0 w-full mb-5 group">
        <input type="text" id="floating_title" formControlName="title" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
        <label for="floating_title" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Título</label>
        <div *ngIf="activityForm.get('title')?.invalid && (activityForm.get('title')?.dirty || activityForm.get('title')?.touched)" class="text-danger">
          El título es requerido.
        </div>
      </div>

      <div class="relative z-0 w-full mb-5 group">
        <select id="category" formControlName="category" class="block py-2.5 px-0 w-full text-sm text-gray-500 bg-transparent border-0 border-b-2 border-gray-200 appearance-none focus:outline-none focus:ring-0 focus:border-gray-500 peer">
          <option value="" disabled>Selecciona tu actividad</option>
          <option value="Domestico">Doméstico</option>
          <option value="Agricultura">Agricultura</option>
          <option value="Educativo">Educativo</option>
          <option value="Medioambiental">Medioambiental</option>
          <option value="Bienestar Animal">Bienestar Animal</option>
          <option value="Salud">Salud</option>
          <option value="Comunidad">Comunidad</option>
          <option value="Culinaria">Culinaria</option>
          <option value="Otros">Otros</option>
        </select>
        <div *ngIf="activityForm.get('category')?.invalid && (activityForm.get('category')?.dirty || activityForm.get('category')?.touched)" class="text-danger">
          La categoría es requerida.
        </div>
      </div>

      <div class="relative z-0 w-full mb-5 group">
        <input type="text" id="floating_Beneficios" formControlName="benefits" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
        <label for="floating_Beneficios" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Beneficios de la Actividad</label>
        <div *ngIf="activityForm.get('benefits')?.invalid && (activityForm.get('benefits')?.dirty || activityForm.get('benefits')?.touched)" class="text-danger">
          Los beneficios son requeridos.
        </div>
      </div>

      <div class="relative z-0 w-full mb-5 group">
        <input type="text" id="floating_ubicacion" formControlName="location" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
        <label for="floating_ubicacion" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Ubicación</label>
        <div *ngIf="activityForm.get('location')?.invalid && (activityForm.get('location')?.dirty || activityForm.get('location')?.touched)" class="text-danger">
          La ubicación es requerida.
        </div>
      </div>
      
      <div class="relative z-0 w-full mb-5 group">
        <input type="text" id="floating_puntoEncuentro" formControlName="meetingPoint" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
        <label for="floating_puntoEncuentro" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Punto de Encuentro</label>
        <div *ngIf="activityForm.get('meetingPoint')?.invalid && (activityForm.get('meetingPoint')?.dirty || activityForm.get('meetingPoint')?.touched)" class="text-danger">
          El punto de encuentro es requerido.
        </div>
      </div>

      <div class="relative z-0 w-full mb-5 group">
        <input type="number" id="floating_personasNecesarias" formControlName="peopleRequired" class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer" placeholder=" " />
        <label for="floating_personasNecesarias" class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Personas Necesarias</label>
        <div *ngIf="activityForm.get('peopleRequired')?.invalid && (activityForm.get('peopleRequired')?.dirty || activityForm.get('peopleRequired')?.touched)" class="text-danger">
          Se requiere al menos 1 persona.
        </div>
      </div>
      
      <div id="date-range-picker" date-rangepicker class="flex items-center">
        <div class="relative">
          <div class="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
              <svg class="w-4 h-4 text-gray-500 dark:text-gray-400" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 20 20">
                <path d="M20 4a2 2 0 0 0-2-2h-2V1a1 1 0 0 0-2 0v1h-3V1a1 1 0 0 0-2 0v1H6V1a1 1 0 0 0-2 0v1H2a2 2 0 0 0-2 2v2h20V4ZM0 18a2 2 0 0 0 2 2h16a2 2 0 0 0 2-2V8H0v10Zm5-8h10a1 1 0 0 1 0 2H5a1 1 0 0 1 0-2Z"/>
              </svg>
          </div>
          <input id="datepicker-range-start" formControlName="startDate" type="text" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full ps-10 p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="Select date start">
        </div>
        <span class="mx-4 text-gray-500">to</span>
        <div class="relative">
          <div class="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
              <svg class="w-4 h-4 text-gray-500 dark:text-gray-400" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 20 20">
                <path d="M20 4a2 2 0 0 0-2-2h-2V1a1 1 0 0 0-2 0v1h-3V1a1 1 0 0 0-2 0v1H6V1a1 1 0 0 0-2 0v1H2a2 2 0 0 0-2 2v2h20V4ZM0 18a2 2 0 0 0 2 2h16a2 2 0 0 0 2-2V8H0v10Zm5-8h10a1 1 0 0 1 0 2H5a1 1 0 0 1 0-2Z"/>
              </svg>
          </div>
          <input id="datepicker-range-end" formControlName="endDate" type="text" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full ps-10 p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="Select date end">
        </div>
        <div *ngIf="activityForm.get('startDate')?.invalid && (activityForm.get('startDate')?.dirty || activityForm.get('startDate')?.touched)" class="text-danger">
          La fecha de inicio es requerida.
        </div>
        <div *ngIf="activityForm.get('endDate')?.invalid && (activityForm.get('endDate')?.dirty || activityForm.get('endDate')?.touched)" class="text-danger">
          La fecha de fin es requerida.
        </div>
      </div>

      <label class="block mb-2 mt-4 text-sm font-medium text-gray-900 dark:text-white" for="file_input">Imagen de la Actividad</label>
      <input class="block w-full text-sm text-gray-900 border border-gray-300 rounded-lg cursor-pointer bg-gray-50 dark:text-gray-400 focus:outline-none dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400" aria-describedby="file_input_help" id="file_input" formControlName="image" type="file" (change)="ShowImage($event)">
      <p class="mt-1 mb-2 text-sm text-gray-500 dark:text-gray-300" id="file_input_help">SVG, PNG, JPG or GIF (MAX. 800x400px).</p>

      <label for="message" class="block mb-2 mt-3 text-sm font-medium text-gray-900 dark:text-white">Descripción de la Actividad</label>
      <textarea id="message" formControlName="description" rows="4" class="block p-2.5 w-full text-sm text-gray-900 bg-gray-50 rounded-lg border border-gray-300 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="Descripción de la Actividad"></textarea>
      <div *ngIf="activityForm.get('description')?.invalid && (activityForm.get('description')?.dirty || activityForm.get('description')?.touched)" class="text-danger">
        La descripción es requerida.
      </div>

      <button type="submit" class="w-full flex justify-center bg-red-600 text-gray-100 p-4 mt-10 rounded-full tracking-wide font-semibold focus:outline-none focus:shadow-outline hover:bg-red-500 shadow-lg cursor-pointer transition ease-in duration-300">
        Crear
      </button>
    </form>
  </div>
</div>
