import { Component } from '@angular/core';
import { NavbarComponent } from '../navbar/navbar.component';

interface Activity {
  id: string;
  title: string;
  description: string;
  image: string;
  location: string;
  date: string;
  detailedDescription: string;
  meetingPoint: string;
  peopleNeeded: number;
}

