import { Component } from '@angular/core';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-navbar',
  standalone: true,
  imports: [ RouterModule ],
  templateUrl: './navbar.component.html',
  styleUrl: './navbar.component.css'
})
export class NavbarComponent {
  currentUserId: number = 0; 
  
  isHost(): boolean {
    return this.currentUserId === 0;
  }

}
<style>
    .header-logo {
      height: 4rem; /* Incrementado el tamaño del logo */
    }
    .search-bar {
      width: 50%;
      max-width: 600px;
      border-radius: 9999px;
      border: 1px solid #D1D5DB; /* Border color gray-300 */
    }
  </style>
  
  <nav class="flex items-center justify-between p-4 bg-white shadow border-b border-gray-300">
    <div class="flex items-center space-x-4">
      <!-- Logo de ManosAmigas -->
      <img src="assets/MLogo.jpg" alt="ManosAmigas Logo" class="header-logo">
    </div>
    
    <div class="flex justify-center flex-1">
      <!-- Barra de búsqueda -->
      <input type="text" placeholder="Buscar actividad..." class="p-2 search-bar">
    </div>
  
    <div class="flex items-center space-x-4">
      <!-- Icono de agregar, visible para usuarios host y voluntarios -->
      <a *ngIf="canShowAddButton()" routerLink="/createActivities">
        <button class="p-2">
          <img src="assets/agregar.png" alt="Add" class="h-6 w-6 md:h-8 md:w-8 lg:h-10 lg:w-10">
        </button>
      </a>
  
      <!-- Icono de perfil -->
      <a routerLink="/createActivities">
        <button class="p-2">
          <img src="assets/login.png" alt="Profile" class="h-6 w-6 md:h-8 md:w-8 lg:h-10 lg:w-10">
        </button>
      </a>
    </div>
  </nav>
