// activity-list.component.ts
import { Component } from '@angular/core';

interface Activity {
  id: string;
  title: string;
  description: string;
  image: string;
  location: string;
  date: string;
}

@Component({
  selector: 'app-activity-list',
  templateUrl: './activity-list.component.html',
  styleUrls: ['./activity-list.component.css']
})
export class ActivityListComponent {
  activities: Activity[] = [
    {
      id: 'actividad1',
      title: 'Doméstico: Ayuda en tareas del hogar',
      description: 'Colabora con familias realizando tareas domésticas como limpieza, cocina y cuidado del hogar, mejorando así su calidad de vida.',
      image: 'assets/domestico.jpeg',
      location: 'Provincia A, Municipio B',
      date: '20/08/2024'
    },
    {
      id: 'actividad2',
      title: 'Agricultura: Apoyo en huertos comunitarios',
      description: 'Participa en la siembra y cosecha de huertos comunitarios, promoviendo la autosuficiencia y el acceso a alimentos frescos y saludables.',
      image: 'assets/agricultura.jpeg',
      location: 'Provincia C, Municipio D',
      date: '22/08/2024'
    },
    {
      id: 'actividad3',
      title: 'Educativo: Clases de apoyo escolar',
      description: 'Ayuda a niños y jóvenes con sus tareas y proyectos escolares, fomentando su desarrollo académico y personal.',
      image: 'assets/educativo.jpeg',
      location: 'Provincia E, Municipio F',
      date: '25/08/2024'
    },
    {
      id: 'actividad4',
      title: 'Medioambiental: Conservación de ecosistemas',
      description: 'Involúcrate en proyectos de conservación y reforestación para proteger nuestros recursos naturales y promover la sostenibilidad ambiental.',
      image: 'assets/medioambiental.jpeg',
      location: 'Provincia G, Municipio H',
      date: '28/08/2024'
    },
    {
      id: 'actividad5',
      title: 'Bienestar Animal: Cuidado y rescate',
      description: 'Colabora en refugios de animales, ayudando con el cuidado, alimentación y rescate de mascotas en situación de calle.',
      image: 'assets/bienestar-animal.jpeg',
      location: 'Provincia I, Municipio J',
      date: '30/08/2024'
    },
    {
      id: 'actividad6',
      title: 'Salud: Jornadas médicas comunitarias',
      description: 'Participa en campañas de salud, ofreciendo atención médica básica y orientación en comunidades con acceso limitado a servicios de salud.',
      image: 'assets/salud.jpeg',
      location: 'Provincia K, Municipio L',
      date: '05/09/2024'
    },
    {
      id: 'actividad7',
      title: 'Comunidad: Integración y desarrollo social',
      description: 'Promueve la integración social y el desarrollo comunitario a través de eventos y actividades que fomenten la cohesión social.',
      image: 'assets/comunidad.jpeg',
      location: 'Provincia M, Municipio N',
      date: '10/09/2024'
    },
    {
      id: 'actividad8',
      title: 'Culinaria: Talleres de cocina saludable',
      description: 'Imparte y asiste a talleres de cocina, aprendiendo y enseñando recetas saludables que promuevan una buena nutrición en la comunidad.',
      image: 'assets/culinaria.jpeg',
      location: 'Provincia O, Municipio P',
      date: '15/09/2024'
    }
  ];

  isModalOpen = false;
  selectedActivity: Activity | null = null;

  showDetails(activity: Activity) {
    this.selectedActivity = activity;
    this.isModalOpen = true;
  }

  closeModal() {
    this.isModalOpen = false;
    this.selectedActivity = null;
  }
}
