import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from '../../services/auth.service'; // Asegúrate de importar el AuthService

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  loginForm: FormGroup;
  errorMessage: string | null = null;

  constructor(private fb: FormBuilder, private authService: AuthService) {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });
  }

  ngOnInit(): void {
    if (this.authService.loggedIn) {
      // Redirige si el usuario ya está logueado
      window.location.href = '/';
    }
  }

  get emailControl() {
    return this.loginForm.get('email');
  }

  get passwordControl() {
    return this.loginForm.get('password');
  }

  login(): void {
    if (this.loginForm.valid) {
      const { email, password } = this.loginForm.value;
      this.authService.login(email, password).subscribe({
        next: (response) => {
          // Manejo exitoso del inicio de sesión
          if (response.success === 1) {
            this.errorMessage = null; // Limpia el mensaje de error si es necesario
          } else {
            this.errorMessage = 'Login failed';
          }
        },
        error: (error) => {
          this.errorMessage = 'Login failed';
        }
      });
    }
  }
}
<div class="min-h-screen flex justify-center py-12 px-4 sm:px-6 lg:px-8 bg-gray-500 bg-no-repeat bg-cover relative items-center"
     style="background-image: url(https://blog.oxfamintermon.org/wp-content/uploads/2015/03/voluntariado.jpg);">
  <div class="absolute bg-black opacity-60 inset-0 z-0"></div>
  <div class="max-w-md w-full space-y-8 p-10 bg-white rounded-xl z-10">
    <div class="text-center">
      <img src="assets/MLogo.jpg" alt="ManosAmigas Logo" class="header-logo h-24 mx-auto">
    </div>
    
    <form [formGroup]="loginForm" (ngSubmit)="login()" class="max-w-md mx-auto">
        <!-- Email Field -->
        <div class="relative z-0 w-full mb-5 group">
          <input type="email" formControlName="email" id="floating_email"
            class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer"
            placeholder=" " required />
          <label for="floating_email"
            class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">
            Email address
          </label>
          <div *ngIf="emailControl?.invalid && emailControl?.touched" class="text-red-600 text-sm">
            <div *ngIf="emailControl?.errors?.['required']">Email is required</div>
            <div *ngIf="emailControl?.errors?.['email']">Email is not valid</div>
          </div>
        </div>
      
        <!-- Password Field -->
        <div class="relative z-0 w-full mb-5 group">
          <input type="password" formControlName="password" id="floating_password"
            class="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none focus:outline-none focus:ring-0 focus:border-red-500 peer"
            placeholder=" " required />
          <label for="floating_password"
            class="peer-focus:font-medium absolute text-sm text-gray-500 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-red-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">
            Password
          </label>
          <div *ngIf="passwordControl?.invalid && passwordControl?.touched" class="text-red-600 text-sm">
            <div *ngIf="passwordControl?.errors?.['required']">Password is required</div>
            <div *ngIf="passwordControl?.errors?.['minlength']">Password must be at least 6 characters long</div>
          </div>
        </div>
      
        <!-- Error Message -->
        <div *ngIf="errorMessage" class="text-red-600 text-sm mb-4">
          {{ errorMessage }}
        </div>
      
        <!-- Submit Button -->
        <button type="submit" [ngClass]="{'bg-red-500': loginForm.valid, 'bg-gray-500': !loginForm.valid}"
          class="w-full flex justify-center text-gray-100 p-4 mt-10 rounded-full tracking-wide font-semibold focus:outline-none focus:shadow-outline hover:bg-red-500 shadow-lg cursor-pointer transition ease-in duration-300"
          [disabled]="!loginForm.valid">
          Login
        </button>
      </form>

  </div>
</div>
