import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { JsonOptions } from './httpOptions'; // Importing the JsonOptions

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = 'https://localhost:7000/api/User/login';

  constructor(private http: HttpClient) {}

  login(email: string, password: string): Observable<any> {
    return this.http.post<any>(this.apiUrl, { email, password }, JsonOptions).pipe( // Using JsonOptions here
      map(response => {
        if (response && response.Data?.token) {
          localStorage.setItem('currentUser', JSON.stringify({ email, token: response.Data.token }));
        }
        return response;
      })
    );
  }

  logout(): void {
    localStorage.removeItem('currentUser');
  }

  public get loggedIn(): boolean {
    return localStorage.getItem('currentUser') !== null;
  }
}
